mapper.select:
SELECT A.USER_ID, A.USER_NM, A.GROUP_ID, A.MBER_TY_CODE, (SELECT CODE_NM FROM IPD_DETAIL_CODE WHERE CODE_ID = 'COM012' AND CODE = A.MBER_TY_CODE AND USE_AT = 'Y') AS MBER_TY_NM, B.AUTHOR_CODE, (CASE WHEN B.SCRTY_DTRMN_TRGET_ID IS NULL THEN 'N' ELSE 'Y' END) AS REG_YN FROM (SELECT USER_ID, USER_NM USER_NM, GROUP_ID, 'USR01' MBER_TY_CODE FROM IPD_MBER ) A LEFT OUTER JOIN IPD_MBER_AUTHOR B ON A.USER_ID = B.SCRTY_DTRMN_TRGET_ID WHERE 1 = 1
AND A.USER_ID LIKE CONCAT('%' , #{searchKeyword}, '%')
AND A.USER_NM LIKE CONCAT('%' , #{searchKeyword}, '%')
AND A.GROUP_ID = #{searchKeyword}
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:

			UPDATE IPD_MBER_AUTHOR 
			   SET MBER_TY_CODE=#{mberTyCode}
                 , AUTHOR_CODE=#{authorCode}
		  	 WHERE SCRTY_DTRMN_TRGET_ID=#{uniqId}
