mapper.insert:

 INSERT INTO IPD_AUTHOR 
       ( AUTHOR_CODE
       , AUTHOR_NM
       , AUTHOR_DC
       , AUTHOR_CREAT_DE )
VALUES ( #{authorCode}
       , #{authorNm}
       , #{authorDc}
       , now())

mapper.update:

UPDATE IPD_AUTHOR 
   SET AUTHOR_NM=#{authorNm}
     , AUTHOR_DC=#{authorDc}
 WHERE AUTHOR_CODE=#{authorCode}

mapper.select:

SELECT AUTHOR_CODE, AUTHOR_NM, AUTHOR_DC, AUTHOR_CREAT_DE
  FROM IPD_AUTHOR 
 WHERE AUTHOR_CODE=#{authorCode}
