mapper.select:

SELECT COUNT(*) AS totcnt
  FROM IPD_PROGRMS
 WHERE PROGRM_FILE_NM = #{searchKeyword}

mapper.insert:

	INSERT INTO IPD_PROGRMS 
		( PROGRM_ID
		  , PROGRM_FILE_NM
		  , PROGRM_STRE_PATH
		  , PROGRM_KOREAN_NM
		  , PROGRM_DC
		  , URL )
	VALUES ( #{progrmId}
	      , #{progrmFileNm}
		  , #{progrmStrePath}
		  , #{progrmKoreanNm}
		  , #{progrmDc}
		  , #{URL} )

mapper.update:

	UPDATE IPD_PROGRMS 
	SET PROGRM_STRE_PATH=#{progrmStrePath}
		, PROGRM_KOREAN_NM=#{progrmKoreanNm}
		, PROGRM_DC      =#{progrmDc}
		, URL            =#{URL}
	WHERE PROGRM_FILE_NM=#{progrmFileNm}
